import './App.css';
import React from 'react'

export default class App extends React.Component {
  constructor(props) {
    super(props);
     this.state = {
       memes: [] 
       ,isFetch: true
   }
 }

  componentDidMount() {
     fetch('https://swapi.dev/api/people/')
      .then(response => response.json())
      .then(starwarsJson => this.setState({ memes: starwarsJson.result, isFetch:false}))
  }

  render() {
    if (this.state.isFetch){
      return 'Loading...'
    }
    const name = this.state.memes[0].displayName
    return <starwarspeople name={name} />
  }   
}
