import React from 'react'
import starwarspeople from './starwarspeople'

class MemeContainer extends React.Component {
  constructor(props) {
    super(props)

     this.state = {
       memes: [], 
       isFetch: true,
   }
 }

  componentDidMount() {
     fetch('http://version1.api.memegenerator.net//Generators_Select_ByPopular?pageIndex=0&pageSize=12&days=&apiKey=demo')
      .then(response => response.json())
      .then(starwarsJson => this.setState({ memes: starwarsJson.result, isFetch:false}))
  }

  render() {
    if (this.state.isFetch){

      return 'Loading...'
    }
    const name = this.state.memes[0].displayName
    return <starwarspeople name={name} />
  }   
}

export default MemeContainer
